  attr(:icon, :atom, required: true)
  attr(:width, :string, default: "30")
  attr(:height, :string, default: "30")
  attr(:class, :any, default: "fill-primary")
  attr(:extra, :global, include: ~w(stroke-width))
  attr(:transform, :string, default: nil)

  @default_icon_attrs %{
    path: "",
    viewbox: "0 0 0 0",
    stroke_linecap: "butt",
    stroke_linejoin: "miter",
    stroke_width: "1",
    fill: "currentFill",
    stroke: "none",
    preserve_aspect_ratio: ""
  }
  def svg(%{icon: icon} = assigns) do
    icon_attrs = Map.get(@path_and_viewbox, icon, %{})

    icon_assigns =
      @default_icon_attrs
      |> Map.merge(icon_attrs)
      |> then(&Map.put_new(&1, :paths, [&1.path]))

    assigns = assign(assigns, icon_assigns)

    ~H"""
    <svg
      xmlns="http://www.w3.org/2000/svg"
      fill={@fill}
      viewBox={@viewbox}
      preserveAspectRatio={@preserve_aspect_ratio}
      stroke-width={@stroke_width}
      stroke={@stroke}
      width={@width}
      height={@height}
      class={@class}
      {@extra}
    >
      <path
        :for={path <- @paths}
        stroke-linecap={@stroke_linecap}
        stroke-linejoin={@stroke_linejoin}
        d={path}
        transform={@transform}
      >
      </path>
    </svg>
    """
  end

  @doc """
  Returns an svg path string for the given key.
  e.g. path(:twitch)
  """
  @spec path(atom()) :: String.t()
  def path(path_key), do: Map.get(@paths, path_key, @paths.empty)

  @doc """
  A map of all the SVG paths
  """
  @spec paths :: %{atom() => String.t()}
  def paths, do: @paths
end
